mapper.select:
SELECT * FROM ( SELECT HAA.area,COUNT(HAEI.id) equipCount FROM hixent_user.hixent_arc_area HAA LEFT JOIN hixent_arc_system.hixent_arc_equipment_info HAEI ON HAA.area_code=substring(HAEI.site_code ,3,2)
AND HAEI.site_code IN
#{item}
WHERE HAA.pid= #{provinceId}
GROUP BY HAA.id)A ORDER BY equipCount DESC

SELECT COUNT(*) FROM (SELECT HAWL.warning_no FROM hixent_arc_system.hixent_arc_warning_list HAWL JOIN hixent_arc_system.hixent_arc_efm_device HAED ON HAED.id=HAWL.efm_id JOIN hixent_arc_system.hixent_arc_site HAS ON HAS.site_code=HAED.site_code WHERE HAWL.etype=1 AND HAWL.is_deal IN (0,2)
AND HAWL.warning_index = #{warnIndex}
GROUP BY HAWL.efm_id )A

SELECT COUNT(*) FROM (SELECT HAWL.warning_no FROM hixent_arc_system.hixent_arc_warning_list HAWL JOIN hixent_arc_system.hixent_arc_equipment_info HAEI ON HAEI.id = HAWL.eid JOIN hixent_arc_system.hixent_arc_site HAS ON HAS.site_code = HAEI.site_code WHERE HAWL.etype IN (0,2) AND HAWL.is_deal IN (0,2) AND HAEI.type=0
AND HAWL.warning_index = #{warnIndex}
GROUP BY HAWL.eid )A

SELECT COUNT(*) FROM (SELECT HAWL.warning_no FROM hixent_arc_system.hixent_arc_warning_list HAWL JOIN hixent_arc_system.hixent_arc_equipment_info HAEI ON HAEI.id=HAWL.eid JOIN hixent_arc_system.hixent_arc_site HAS ON HAS.site_code=HAEI.site_code WHERE HAWL.etype IN (0,2) AND HAWL.is_deal IN (0,2) AND HAEI.type=1
AND HAWL.warning_index = #{warnIndex}
GROUP BY HAWL.eid )A
