SELECT HAWL.eid,HAWL.etype,HAWL.warning_no,HAWL.warning_time,HAWL.is_deal,
HAS.site_name,HAS.site_code,HAP.protocol_node,HAED.device_code,HAED.niName,HAED.specificator,HAWL.isReset
FROM hixent_arc_system.hixent_arc_warning_list HAWL 
JOIN hixent_arc_system.hixent_arc_efm_device HAED ON HAWL.efm_id=HAED.id
 JOIN hixent_arc_system.hixent_arc_site HAS ON HAS.site_code=HAED.site_code
JOIN hixent_arc_protocol HAP ON HAP.protocol_name=HAWL.warning_type
 WHERE HAWL.is_deal IN (0,2) AND HAWL.etype=1 

 AND HAWL.warning_index=#{warnIndex}



  AND HAS.site_id=#{siteId}


  AND HAED.id=#{deviceId}

 ORDER BY HAWL.warning_time DESC


 limit ${limits}

SELECT COUNT(HAWL.warning_no)
FROM hixent_arc_system.hixent_arc_warning_list HAWL 
JOIN hixent_arc_system.hixent_arc_efm_device HAED ON HAWL.efm_id=HAED.id
 JOIN hixent_arc_system.hixent_arc_site HAS ON HAS.site_code=HAED.site_code
JOIN hixent_arc_protocol HAP ON HAP.protocol_name=HAWL.warning_type
 WHERE HAWL.is_deal IN (0,2) AND HAWL.etype=1 

 AND HAWL.warning_index=#{warnIndex}



  AND HAS.site_id=#{siteId}


  AND HAED.id=#{deviceId}

SELECT HAWL.eid,HAWL.etype,HAWL.warning_no,HAWL.warning_time,HAS.site_name,HAWL.is_deal, HAS.site_code,HAP.protocol_node,HAED.device_code,HAED.line_code,HAED.addr,HAEV.niName,HAEV.specificator ,GROUP_CONCAT(DISTINCT HAU.account ORDER BY appUser_id DESC ) huid ,GROUP_CONCAT(DISTINCT HAU.mobile ORDER BY appUser_id DESC ) hmobile ,HAED.type,HAED.equip_place,HAED.tempration,HAEV.niName,HAWL.isReset FROM hixent_arc_system.hixent_arc_warning_list HAWL JOIN hixent_arc_system.hixent_arc_equipment_info HAED ON HAWL.eid=HAED.id LEFT JOIN hixent_arc_system.hixent_arc_efm_device HAEV ON HAEV.id=HAED.efm_id LEFT JOIN hixent_app_control_group HACP ON HACP.id=HAED.equip_group_id LEFT JOIN hixent_app_user_group_link HAUGL ON HAUGL.group_id=HAED.equip_group_id LEFT JOIN hixent_app_user HAU ON HAU.id=HAUGL.appUser_id LEFT JOIN hixent_arc_system.hixent_arc_site HAS ON HAS.site_code=HAED.site_code LEFT JOIN hixent_arc_protocol HAP ON HAP.protocol_name=HAWL.warning_type WHERE HAWL.is_deal IN (0,2) AND HAWL.etype IN (0,2)
AND HAWL.warning_index=#{warnIndex}
AND HAS.site_id=#{siteId}
AND HAEV.id=#{deviceId}
AND HAED.type=#{type}
AND HAED.addr LIKE CONCAT(CONCAT('%', #{inquire}),'%')
GROUP BY HAWL.warning_no ORDER BY HAWL.warning_time DESC
limit ${limits}

SELECT COUNT(*) FROM (SELECT COUNT(HAWL.warning_no) FROM hixent_arc_system.hixent_arc_warning_list HAWL JOIN hixent_arc_system.hixent_arc_equipment_info HAED ON HAWL.eid=HAED.id LEFT JOIN hixent_arc_system.hixent_arc_efm_device HAEV ON HAEV.id=HAED.efm_id LEFT JOIN hixent_app_control_group HACP ON HACP.id=HAED.equip_group_id LEFT JOIN hixent_app_user_group_link HAUGL ON HAUGL.group_id=HAED.equip_group_id LEFT JOIN hixent_app_user HAU ON HAU.id=HAUGL.appUser_id LEFT JOIN hixent_arc_system.hixent_arc_site HAS ON HAS.site_code=HAED.site_code LEFT JOIN hixent_arc_protocol HAP ON HAP.protocol_name=HAWL.warning_type WHERE HAWL.is_deal IN (0,2) AND HAWL.etype IN (0,2)
AND HAWL.warning_index=#{warnIndex}
AND HAS.site_id=#{siteId}
AND HAEV.id=#{deviceId}
AND HAED.type=#{type}
AND HAED.addr LIKE CONCAT(CONCAT('%', #{inquire}),'%')
GROUP BY HAWL.warning_no )A

SELECT HAWL.eid,HAWL.etype,HAWL.warning_no,HAWDL.defe_dispatch_time,HAWL.warning_time, HAS.site_name,HAS.site_code,HAP.protocol_node,HAED.device_code,HAED.niName,HAU.account,HAU.mobile appMobile,HAED.specificator FROM hixent_arc_system.hixent_arc_warning_list HAWL JOIN hixent_arc_system.hixent_arc_warn_deal_feedback HAWDL ON HAWDL.defe_warn_id= HAWL.warning_no JOIN hixent_user.hixent_app_user HAU ON HAU.id=HAWDL.defe_appUser_id JOIN hixent_arc_system.hixent_arc_efm_device HAED ON HAWL.efm_id=HAED.id JOIN hixent_arc_system.hixent_arc_site HAS ON HAS.site_code=HAED.site_code JOIN hixent_arc_protocol HAP ON HAP.protocol_name=HAWL.warning_type WHERE HAWL.is_deal=1 AND HAWL.etype=1
AND HAWL.dispatch_time>#{startTime}
AND HAWL.dispatch_time<#{endTime}
ORDER BY HAWL.warning_time DESC
limit ${limits}

SELECT COUNT(HAWL.warning_no) FROM hixent_arc_system.hixent_arc_warning_list HAWL JOIN hixent_arc_system.hixent_arc_warn_deal_feedback HAWDL ON HAWDL.defe_warn_id= HAWL.warning_no JOIN hixent_user.hixent_app_user HAU ON HAU.id=HAWDL.defe_appUser_id JOIN hixent_arc_system.hixent_arc_efm_device HAED ON HAWL.efm_id=HAED.id JOIN hixent_arc_system.hixent_arc_site HAS ON HAS.site_code=HAED.site_code JOIN hixent_arc_protocol HAP ON HAP.protocol_name=HAWL.warning_type WHERE HAWL.is_deal=1 AND HAWL.etype=1
AND HAWL.dispatch_time >= #{startTime}
AND HAWL.dispatch_time <= #{endTime}

SELECT HAWL.eid,HAWL.etype,HAWL.warning_no,HAWL.dispatch_time,HAS.site_name,HAS.site_code, HAP.protocol_node,HAED.device_code,HAED.line_code,HAED.addr,HAEV.specificator ,GROUP_CONCAT(DISTINCT HAU.account ORDER BY appUser_id DESC ) huid ,GROUP_CONCAT(DISTINCT HAU.mobile ORDER BY appUser_id DESC ) hmobile ,HAED.type,HAED.equip_place,HAWL.app_name,HAWL.warning_time,HAWL.node,HAEV.niName,HAU.account,HAU.mobile appMobile FROM hixent_arc_system.hixent_arc_warning_list HAWL JOIN hixent_arc_system.hixent_arc_equipment_info HAED ON HAWL.eid=HAED.id JOIN hixent_arc_system.hixent_arc_efm_device HAEV ON HAEV.id=HAED.efm_id JOIN hixent_arc_system.hixent_arc_warn_deal_feedback HAWDL ON HAWDL.defe_warn_id= HAWL.warning_no JOIN hixent_user.hixent_app_user HAU ON HAU.id=HAWDL.defe_appUser_id
LEFT JOIN hixent_user.hixent_app_control_group HACP ON HACP.id=HAED.equip_group_id LEFT JOIN hixent_user.hixent_app_user_group_link HAUGL ON HAUGL.group_id=HAED.equip_group_id
LEFT JOIN hixent_arc_system.hixent_arc_site HAS ON HAS.site_code=HAED.site_code LEFT JOIN hixent_arc_protocol HAP ON HAP.protocol_name=HAWL.warning_type WHERE HAWL.is_deal=1 AND HAWL.etype=2
AND HAWL.dispatch_time > #{startTime}
AND HAWL.dispatch_time < #{endTime}
AND HAED.type=#{type}
AND (HAED.addr LIKE CONCAT(CONCAT('%', #{inquire}),'%')
GROUP BY HAWL.warning_no ORDER BY HAWL.warning_time DESC
limit ${limits}

