LEFT JOIN hixent_user.hixent_app_control_group HACP ON HACP.id=HAED.equip_group_id LEFT JOIN hixent_user.hixent_app_user_group_link HAUGL ON HAUGL.group_id=HAED.equip_group_id
LEFT JOIN hixent_arc_system.hixent_arc_site HAS ON HAS.site_code=HAED.site_code LEFT JOIN hixent_arc_protocol HAP ON HAP.protocol_name=HAWL.warning_type WHERE HAWL.is_deal=1 AND HAWL.etype=2
