GROUP BY HAWL.warning_no )A

SELECT HAWDF.*,HAWL.warning_time,HAP.account,HAP.mobile FROM hixent_arc_system.hixent_arc_warn_deal_feedback HAWDF 
JOIN hixent_arc_system.hixent_arc_warning_list HAWL ON HAWL.warning_no=HAWDF.defe_warn_id
JOIN hixent_arc_system.hixent_arc_efm_device HAED ON HAED.id=HAWL.efm_id
JOIN hixent_arc_system.hixent_arc_site HAS ON HAED.site_code=HAS.site_code
JOIN hixent_app_user HAP ON HAP.id=HAWDF.defe_appUser_id
WHERE HAWDF.defe_id>0


   AND HAWDF.defe_dispatch_time > #{startTime}


   AND HAWDF.defe_dispatch_time < #{endTime}


 AND HAWDF.defe_is_audit=#{type}


  limit ${limits}

SELECT COUNT(*) FROM hixent_arc_system.hixent_arc_warn_deal_feedback HAWDF 
JOIN hixent_arc_system.hixent_arc_warning_list HAWL ON HAWL.warning_no=HAWDF.defe_warn_id
JOIN hixent_arc_system.hixent_arc_efm_device HAED ON HAED.id=HAWL.efm_id
JOIN hixent_arc_system.hixent_arc_site HAS ON HAED.site_code=HAS.site_code
JOIN hixent_app_user HAP ON HAP.id=HAWDF.defe_appUser_id
WHERE HAWDF.defe_id>0


   AND HAWDF.defe_dispatch_time > #{startTime}


   AND HAWDF.defe_dispatch_time < #{endTime}


 AND HAWDF.defe_is_audit=#{type}


  limit ${limits}

SELECT HAWL.eid,HAWL.etype,HAWL.warning_no,HAWDL.defe_dispatch_time,HAWL.warning_time,HAWL.dispatch_time, HAS.site_name,HAS.site_code,HAP.protocol_node,HAED.device_code,HAED.niName,HAED.specificator, HAU.account,HAU.mobile appMobile,HAEI.line_code,HAEI.addr,HAWL.isReset FROM hixent_arc_system.hixent_arc_warning_list HAWL LEFT JOIN hixent_arc_system.hixent_arc_warn_deal_feedback HAWDL ON HAWDL.defe_warn_id= HAWL.warning_no LEFT JOIN hixent_user.hixent_app_user HAU ON HAU.id=HAWDL.defe_appUser_id LEFT JOIN hixent_arc_system.hixent_arc_efm_device HAED ON HAWL.efm_id=HAED.id LEFT JOIN hixent_arc_system.hixent_arc_equipment_info HAEI ON HAEI.id=HAWL.eid LEFT JOIN hixent_arc_system.hixent_arc_site HAS ON HAS.site_code=HAED.site_code OR HAS.site_code=HAEI.site_code JOIN hixent_arc_protocol HAP ON HAP.protocol_name=HAWL.warning_type WHERE HAWL.is_deal=1
AND HAWDL.defe_dispatch_time > #{startTime}
AND HAWDL.defe_dispatch_time < #{endTime}
ORDER BY HAWL.dispatch_time DESC
limit ${limits}

